    }

    &:hover {
        background-color: $dark-blue;
        transition: background 0.2s;
    }
}

.header {
    display: flex;
    background-color: $blue;
    justify-content: space-between;
    align-items: center;
    color: #fff;
}

.header__content {
    display: flex;
    align-items: center;
}

.header__title {
    display: flex;
    width: 3em;
    height: $height;
    margin-top: 3px;
    padding: 0 8px;
    align-items: center;
    font-size: 1.6rem;
    font-weight: bold;
}

.header__search {
    $search-height: 36px;
    position: relative;
    width: 100%;
    justify-items: stretch;

    .header__search__search-btn,
    .header__search__cancel-btn {
        position: absolute;
        width: $search-height;
        height: $search-height;

        img {
            margin-top: 3px;
            width: 16px;
            height: 16px;
        }
    }

    .header__search__search-btn {
        top: 0;
        left: 0;

        @include tooltip("left", "검색", 40px);
    }
    .header__search__cancel-btn {
        top: 0;
        right: 0;

        @include tooltip("bottom", "검색 종료", 80px);
    }

    .header__search__input {
        width: 100%;
        padding-left: 40px;
        height: $search-height;
        border-radius: 2px;
        box-sizing: border-box;
    }

    @media screen and (min-width: $mobile) {
        max-width: 400px;
    }
}

sources.scss/common/_tooltip.scss:
@mixin tooltip($direction, $message: "", $width: 120px) {
    &:before,
    &:after {
        opacity: 0;
        transition: opacity 0.12s ease;
        z-index: 100;
        box-shadow: 2px 2px 5px 0 rgba(178, 178, 178, 0.2);
    }

    &:hover {
        &:before {
            content: "";
            position: absolute;
            opacity: 1;
            background-color: #fff;
            width: 16px;
            height: 16px;

            @if $direction == "top" {
                bottom: 117%;
                left: 50%;
                transform: translateX(-50%) rotate(45deg);
                border-bottom: 1px solid #eaeaea;
                border-right: 1px solid #eaeaea;
            } @else if $direction == "bottom" {
                top: 117%;
                left: 50%;
                transform: translateX(-50%) rotate(45deg);
                border-top: 1px solid #eaeaea;
                border-left: 1px solid #eaeaea;
            } @else if $direction == "right" {
                top: 50%;
                left: 117%;
                transform: translateY(-50%) rotate(45deg);
                border-bottom: 1px solid #eaeaea;
                border-left: 1px solid #eaeaea;
            } @else {
                top: 50%;
                right: 117%;
                transform: translateY(-50%) rotate(45deg);
                border-top: 1px solid #eaeaea;
                border-right: 1px solid #eaeaea;
            }
        }

        &:after {
            content: $message;
            opacity: 1;
            position: absolute;
            width: $width;
            height: 36px;
            padding: 9px 7px;
            background-color: #fff;
            border: 1px solid #eaeaea;
            box-sizing: border-box;

            @if $direction == "top" {
                bottom: 140%;
                left: 50%;
                transform: translateX(-50%);
            } @else if $direction == "bottom" {
                top: 140%;
                left: 50%;
                transform: translateX(-50%);
            } @else if $direction == "right" {
                top: 50%;
                left: 140%;
                transform: translateY(-50%);
            } @else {
                top: 50%;
                right: 140%;
                transform: translateY(-50%);
            }
        }
    }
}

